delimiter ;;
CREATE FUNCTION `getUserCount`(`in_department_sn` varchar(50))
 RETURNS int
BEGIN
	declare cnt int;
	select count(*) into cnt from `user` where department_sn like concat(in_department_sn,'%');
	RETURN cnt;
END
;;
